e3c kh\\u1ea3 n\\u0103ng c\\u1ee7a m\\u00ecnh t\\u1eeb m\\u1ed9t v\\u1ebft c\\u1eafn c\\u1ee7a nh\\u1ec7n, Parker \\u0111\\u00e3 ch\\u1ecdn \\u0111\\u1ec3 b\\u1ea3o v\\u1ec7 Queens kh\\u1ecfi t\\u1ed9i \\u00e1c v\\u1edbi quy\\u1ec1n h\\u1ea1n c\\u1ee7a m\\u00ecnh, tung h\\u1ee9ng t\\u1ea5t c\\u1ea3 c\\u00e1c nhi\\u1ec7m v\\u1ee5 si\\u00eau anh h\\u00f9ng c\\u1ee7a m\\u00ecnh v\\u00e0 nhu c\\u1ea7u c\\u1ee7a cu\\u1ed9c s\\u1ed1ng trung h\\u1ecdc c\\u1ee7a m\\u00ecnh. M\\u1eb7c d\\u00f9 c\\u00f3 nh\\u1eefng n\\u1ed7 l\\u1ef1c t\\u1ed1t nh\\u1ea5t \\u0111\\u1ec3 gi\\u1eef b\\u00ed m\\u1eadt danh t\\u00ednh c\\u1ee7a m\\u00ecnh t\\u1eeb th\\u1ebf gi\\u1edbi, \\u00f4ng \\u0111\\u00e3 \\u0111\\u01b0\\u1ee3c Tony Stark t\\u00ecm th\\u1ea5y v\\u00e0 tuy\\u1ec3n d\\u1ee5ng trong cu\\u1ed9c N\\u1ed9i chi\\u1ebfn Avengers, trong khi gi\\u00e0nh \\u0111\\u01b0\\u1ee3c m\\u1ed9t b\\u1ed9 \\u0111\\u1ed3 v\\u00e0 c\\u00f4ng ngh\\u1ec7 m\\u1edbi \\u0111\\u1ec3 \\u0111\\u1ed5i l\\u1ea5y s\\u1ef1 gi\\u00fap \\u0111\\u1ee1 c\\u1ee7a \\u00f4ng<\\/p>\",\"_id\":\"940a3c2\"},{\"tab_title\":\"Mr. Negative\",\"tab_content\":\"<p><img class=\\\"alignnone size-full wp-image-55\\\" src=\\\"http:\\/\\/localhost\\/marvel\\/wp-content\\/uploads\\/2018\\/04\\/Mr.negative.jpg\\\" alt=\\\"\\\" width=\\\"200\\\" height=\\\"200\\\" \\/><\\/p><p>M\\u1ed9t ng\\u01b0\\u1eddi sau n\\u00e0y l\\u1ea5y t\\u00ean c\\u1ee7a Martin Li, \\u0111\\u00e3 \\u0111\\u01b0\\u1ee3c \\u0111\\u01b0a ra m\\u1ed9t lo\\u1ea1i thu\\u1ed1c th\\u1eed nghi\\u1ec7m m\\u00e0 cu\\u1ed1i c\\u00f9ng cho ph\\u00e9p anh ta ki\\u1ec3m so\\u00e1t m\\u1ed9t s\\u1ed1 lo\\u1ea1i n\\u0103ng l\\u01b0\\u1ee3ng \\u0111i\\u1ec7n m\\u00e0u \\u0111en gi\\u1ed1ng nh\\u01b0 m\\u1ed9t \\u00e2m b\\u1ea3n nhi\\u1ebfp \\u1ea3nh<\\/p>\",\"_id\":\"0ca368d\"}],\"type\":\"vertical\",\"navigation_width\":{\"unit\":\"%\",\"size\":15},\"border_color\":\"#ff0000\",\"tab_color\":\"#b20500\",\"tab_active_color\":\"#52ed2f\",\"content_color\":\"#000000\"},\"elemen